package EProgramm;

public class SMMETHOD1 {
	static double sum(double num1, double num2){
		double res;
		res = num1+ num2;
		return res;
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double x = SMMETHOD1.sum(3, 10.2);
		System.out.println(x);
	}

}
